mapper.select:
select distinct u.user_id, u.dept_id, u.login_name, u.real_name, u.email, u.mobile, u.status, u.create_time from s_user u left join s_dept d on u.dept_id = d.dept_id left join s_user_role ur on u.user_id = ur.user_id left join s_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.real_name like concat('%', #{realName}, '%')
AND u.mobile like concat('%', #{mobile}, '%')
